import React, {useEffect, useState} from 'react';
import './App.css';
// import { FiRefreshCcw } from 'react-icons/fi';
// import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { library } from "@fortawesome/fontawesome-svg-core";
import { fas } from "@fortawesome/free-solid-svg-icons";
import Amplify, {API} from 'aws-amplify';
import { withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import awsconfig from './aws-exports';
import * as mutations from './graphql/mutations';
import * as queries from './graphql/queries';
import toast from "light-toast"
Amplify.configure(awsconfig);
library.add(fas)

function App() {

  useEffect(() => {
    updateVals();
    const totalValues = mylist.map((n) => console.log('n->',n))
      // console.log('total->',totalValues);
  }, []);

  const [mylist, setMyList] = useState([]);
  const [loop, setLoop] = useState(10);

  const updateVals = async() =>{
      const result = await API.graphql({
        query: queries.listWindspeeds,
        fetchPolicy: "no-cache",
      });
      setMyList(result.data.listWindspeeds.items);
      // console.log(result.data.listWindspeeds.items);
    };

  function sleep(milliseconds) {
    const date = Date.now();
    let currentDate = null;
    do {
      currentDate = Date.now();
    } while (currentDate - date < milliseconds);
  }

  async function genValues () {
    setLoop(4);
    // alert("Data generator started");
    for (let i = 0; i < loop; i++) {
    const generateValues = { value: Math.floor(Math.random() * 25) + 25};
    await API.graphql({ query: mutations.createWindspeed, variables: { input: generateValues}});
    updateVals();
    sleep(3000);
    }
  }

  function updateYes () {
    toast.success('Starting...');
    genValues();
    updateVals();
  }

  function updateNo () {
    toast.success('Stopping...');
    setLoop(0);
    updateVals();
  }

  const refresh = () => {
    // alert("refreshing");
    toast.success('Refreshing...');
    updateVals();
  }

  const generateKeyValePair = (objdata, arraytoupdated) => {
    Object.keys(objdata).forEach(function(key) {
    var objNeeded = { name: 'init', value: -1};
    objNeeded['name'] = key;
    objNeeded['value'] = objdata[key];
    arraytoupdated.push(objNeeded);
    // console.log('key->', mylist);
  });}

  const myChartArray = [];
  generateKeyValePair(mylist, myChartArray);
  console.log('keypair->',myChartArray);


  return (
    <div className="App">
      <AmplifySignOut />
      <header className="App-header">

        <h1>Wind turbine data</h1>
        <div className="App-buttons">
        <button className="App-buttonsYes" onClick={updateYes}>Generate</button>
        {/* <button className="App-buttonsNo" onClick={updateNo}>Stop</button> */}
        {/* <FiRefreshCcw ><button className="App-buttonsNo" onClick={refresh}>Refresh</button></FiRefreshCcw> */}
        {/* <FontAwesomeIcon icon={['fas', 'sync']} className="fa-spin"></FontAwesomeIcon> */}
        {/* <FontAwesomeIcon icon={faHome} /> */}
        {/* <FiRefreshCcw onClick={refresh} /> */}
        </div>

        <ul>
        <h3>DATA</h3>
        {mylist.map((item, index) => (
          <p key={index}>{item.value}</p>
        ))}
      </ul>
      </header>
    </div>
  );
}

export default withAuthenticator(App);
