import React, {useEffect} from 'react';
import './App.css';
import Amplify, {API} from 'aws-amplify';
import { withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import awsconfig from './aws-exports';
import * as mutations from './graphql/mutations';
import * as queries from './graphql/queries';
Amplify.configure(awsconfig);


function App() {

  // const todoInit = {
  //   name: 'Subscribe',
  //   value: 'No'
  // };

  const todoUpdateYes = {
    name: 'Subscribe',
    value: 'Yes'
  };

  const todoUpdateNo = {
    name: 'Subscribe',
    value: 'No'
  };

  useEffect(() => {
    async function fetchData() {
    const newTodo = await API.graphql({ query: queries.listTodos});
  }
  fetchData();
  }, []);

  async function updateYes () {
    await API.graphql({ query: mutations.createTodo, variables: {input: todoUpdateYes}});
    alert("You are now subscribed")
  }

  async function updateNo () {
    await API.graphql({ query: mutations.createTodo, variables: {input: todoUpdateNo}});
    alert("You are now unsubscribed")
  }

  return (
    <div className="App">
      <header >
        <h1>Would you like to Subscribe?</h1>
        <div className="App-buttons">
        <button className="App-buttonsYes" onClick={updateYes}>Yes</button>
        <button className="App-buttonsNo" onClick={updateNo}>No</button>
        </div>
      </header>
    </div>
  );
}

export default withAuthenticator(App);
