import React, {useEffect} from 'react';
import './App.css';
import Amplify, {API, graphqlOperation} from 'aws-amplify';
import { withAuthenticator, AmplifySignOut } from '@aws-amplify/ui-react';
import awsconfig from './aws-exports';
import * as mutations from './graphql/mutations';
Amplify.configure(awsconfig);


function App() {

  const todoInit = {
    name: 'Subscribe',
    value: 'No'
  };

  const todoUpdateYes = {
    name: 'Subscribe',
    value: 'No'
  };

  const todoUpdateNo = {
    name: 'Subscribe',
    value: 'No'
  };

  useEffect(() => {
    async function fetchData() {
    const newTodo = await API.graphql({ query: mutations.createTodo, variables: {input: todoInit}});
  }
  fetchData();
  }, []);

  async function updateYes () {
    await API.graphql({ query: mutations.createTodo, variables: {input: todoUpdateYes}});
  }

  async function updateNo () {
    await API.graphql({ query: mutations.createTodo, variables: {input: todoUpdateNo}});
  }

  return (
    <div className="App">
      <header className="App-header">
        Would you like to Subscribe?
        <button onClick={updateYes}>Yes</button>
        <button onClick={updateNo}>No</button>
      </header>
    </div>
  );
}

export default withAuthenticator(App);
